  namespace osCommerce\OM\Core\Site\Admin\Application\ServerInfo;

  use osCommerce\OM\Core\Registry;
  use osCommerce\OM\Core\OSCOM;
  use osCommerce\OM\Core\DateTime;
  
	class ServerInfo {

/* Class constructor */

    function __construct() {

      $this->_image = '';

      if ( !isset($_GET['action']) ) {
        $_GET['action'] = '';
      }

      if ( !empty($_GET['action']) ) {
        switch ( $_GET['action'] ) {
          case 'image':
            header('Content-Type: image/gif');

            echo base64_decode($this->_image);

            exit;

            break;

          case 'phpInfo':
            phpinfo();

            exit;

            break;
        }
      }
    }
    public static function getServerInfo() {

			$OSCOM_Database = Registry::get('Database');
	
	    $Qdb_date = $OSCOM_Database->query('select now() as datetime');
	    $Qdb_date->execute();
	    $Qdb_uptime = $OSCOM_Database->query('show status like "Uptime"');
			//$Qdb_uptime->execute();

	    @list($system, $host, $kernel) = preg_split('/[\s,]+/', @exec('uname -a'), 5);
	    
	    $db_uptime = intval($Qdb_uptime->valueInt('Value') / 3600) . ':' . str_pad(intval(($Qdb_uptime->valueInt('Value') / 60) % 60), 2, '0', STR_PAD_LEFT);
	
	    return array('date' => DateTime::getShort(null, true),
	                 'system' => $system,
	                 'kernel' => $kernel,
	                 'host' => $host,
	                 'ip' => gethostbyname($host),
	                 'uptime' => @exec('uptime'),
	                 'http_server' => $_SERVER['SERVER_SOFTWARE'],
	                 'php' => PHP_VERSION,
	                 'zend' => (function_exists('zend_version') ? zend_version() : ''),
	                 'db_server' => DB_SERVER,
	                 'db_ip' => gethostbyname(DB_SERVER),
	                 'db_version' => 'MySQL ' . $OSCOM_Database->getServerVersion(),
	                 'db_date' => DateTime::getShort($Qdb_date->value('datetime'), true),
	                 'db_uptime' => $db_uptime);
		}
  }
?>
